import java.util.*;import com.google.common.collect.*;public class CountingMap<K,V>    extends ForwardingMap<K,V>{    private Map<K,V> theStore;        public CountingMap(Map<K,V> storage)    {        theStore = storage;    }    public CountingMap()    {        this(new HashMap<K,V>());    }        public Map<K,V> delegate() { return theStore; }        private int getCount = 0;    public V get(Object key)    {        System.out.println(++getCount);        return base.get(key);    }}